CREATE PROCEDURE updateMedia(IN id_in INT,
														 IN url_in VARCHAR(50))
BEGIN
	UPDATE media
	SET url = url_in
	WHERE id = id_in;
END//

CREATE PROCEDURE deletePost(In id_in INT)
BEGIN
	DECLARE media_id INT DEFAULT 0;
	SELECT media_id INTO media_id
		FROM post
		WHERE id = id_in;
	DELETE FROM media
	WHERE id = media_id;
	DELETE FROM post
	WHERE id = id_in;
END//

CREATE PROCEDURE deleteMember(In id_in INT)
BEGIN
	DELETE FROM member
	WHERE id = id_in;
END//

CREATE PROCEDURE deleteComment(In id_in INT)
BEGIN
	DELETE FROM comments
	WHERE id = id_in;
END//

CREATE PROCEDURE getMediaUrl(IN url_in VARCHAR(100))
BEGIN
	SELECT id FROM media
	WHERE url LIKE url_in;
END//

